(41, 'cookieprefix', 'Cookie Prefix', 'A prefix to append to all cookies set by MyBB. This is useful if you wish to install multiple copies of MyBB on the one domain or have other software installed which conflicts with the names of the cookies in MyBB. If not specified, no prefix will be used.', 'text', '', 10, 7, 1),
(42, 'showvernum', 'Show Version Numbers', 'Allows you to turn off the public display of version numbers in MyBB.', 'onoff', '0', 11, 7, 1),
(43, 'captchaimage', 'CAPTCHA Images for Registration & Posting', 'If yes, and GD is installed, an image will be shown during registration and posting where users are required to enter the text contained within the image to continue. This helps prevent automated registrations and postings.', 'onoff', '1', 12, 7, 1),
(44, 'reportmethod', 'Reported Posts Medium', 'Please select from the list how you want reported posts to be dealt with. Storing them in the database is probably the better of the options listed.', 'radio\ndb=Stored in the Database\npms=Sent as Private Messages\nemail=Sent via Email', 'db', 13, 7, 1),
