(52, 'mail_handler', 'Mail handler', 'The medium through which MyBB will send outgoing emails.', 'select\nmail=PHP mail\nsmtp=SMTP mail', 'mail', 1, 8, 1),
(53, 'smtp_host', 'SMTP hostname', 'The hostname of the SMTP server through which mail should be sent.<br />Only required if SMTP Mail is selected as the Mail Handler.', 'text', '', 2, 8, 1),
(54, 'smtp_port', 'SMTP port', 'The port number of the SMTP server through which mail should be sent.<br />Only required if SMTP Mail is selected as the Mail Handler.', 'text', '', 3, 8, 1),
(55, 'smtp_user', 'SMTP username', 'The username used to authenticate with the SMTP server.<br />Only required if SMTP Mail is selected as the Mail Handler, and the SMTP server requires authentication.', 'text', '', 4, 8, 1),
