(57, 'secure_smtp', 'SMTP Encryption Mode', 'Select the encryption required to communicate with the SMTP server.<br />Only required if SMTP Mail is selected as the Mail Handler.', 'select\n0=No encryption\n1=SSL encryption\n2=TLS encryption', '0', 6, 8, 1),
(58, 'mail_parameters', 'Additional Parameters for PHP''s mail()', 'This setting allows you to set additional parameters for the PHP mail() function. Only used when ''PHP mail'' is selected as Mail Handler. <a href="http://php.net/function.mail" target="_blank">More information</a>', 'text', '', 7, 8, 1),
(59, 'mail_logging', 'Mail Logging', 'This setting allows you to set how to log outgoing emails sent via the ''Send Thread to a Friend'' feature. In some countries it is illegal to log all content.', 'select\n0=Disable email logging\n1=Log emails without content\n2=Log everything', '2', 8, 8, 1),
(60, 'mail_message_id', 'Add message ID in mail headers', 'Disabling this option on some shared hosts resolves issues with forum emails being marked as spam.', 'yesno', '1', 9, 8, 1),
