(195, 'google_seo_404_widget', '404 widget', 'Add the Google 404 widget for invalid thread / forum / etc error pages.', 'yesno', '1', 2, 22, 0),
(196, 'google_seo_404_wol_show', 'Show 404 errors in Who''s Online', 'Specify if you want to show that users are seeing the 404 error page in the Who''s Online list. This is not recommended. Enabling this can cause problems such as spambots showing up as guests, or users showing up as seeing error pages if your forum e.g. tries to include an image that does not exist.', 'yesno', '1', 3, 22, 0),
(197, 'google_seo_meta', 'Google SEO Meta', 'This module generates meta tags for the current page. Please see the <a href="../inc/plugins/google_seo.txt">documentation</a> for details.<br /><br />Set to YES to enable Google SEO Meta. Setting this to NO also disables all other settings in this group.', 'yesno', '1', 1, 23, 0),
(198, 'google_seo_meta_length', 'Meta description', 'Generate Meta description tags based on the contents of the current page (description of a forum, first posting of a thread, ...). Set to the maximum description length you want to allow or to 0 to disable.', 'text', '200', 2, 23, 0),
(199, 'google_seo_meta_canonical', 'Canonical Page', 'Specify a canonical page. This helps avoid Google indexing the same page under several different names. Please see <a href="http://www.google.com/support/webmasters/bin/answer.py?hl=en&amp;answer=139394">About rel="canonical"</a> for details.', 'yesno', '1', 3, 23, 0),
(200, 'google_seo_meta_archive', 'Add tags to Archive', 'Enable this option if you want tags to be added to MyBB''s Lite (Archive) Mode pages by using unconventional methods.', 'yesno', '1', 4, 23, 0),
(201, 'google_seo_redirect', 'Google SEO Redirect', 'This module redirects old and invalid URLs to their current proper names. This can be used for all sorts of redirections: redirect to the main site if your forum is available under several domain names, redirect stock MyBB URLs to Google SEO URLs (or the other way around). This prevents your users and Google from seeing the same page under several different names. Please see the <a href="../inc/plugins/google_seo.txt">documentation</a> for details.<br /><br />Set to YES to enable Google SEO Redirect. Setting this to NO also disables all other settings in this group.', 'yesno', '1', 1, 24, 0),
(202, 'google_seo_redirect_permission', 'Permission Checks', 'Should Redirect let permission checks run first? Enabling this option will prevent Redirect from redirecting URLs for items that the user is not allowed to access anyway. This is probably only necessary if you''re also using SEO URLs and you''re concerned about users getting redirected to the SEO URL of a forum / thread they''re not allowed to read, which would give away the subject in the SEO URL.', 'yesno', '1', 2, 24, 0),
(203, 'google_seo_redirect_litespeed', 'LiteSpeed Bug workaround', 'If your server is running LiteSpeed &lt;= 4.0.10 instead of Apache, and you see redirection loops on member profile / send mail or calendar event / edit event pages, you are suffering from bug in LiteSpeed''s mod_rewrite replacement. Set to YES to work around this bug - Google SEO Redirect will then leave the problematic pages alone. Apache / Nginx / lighttpd users can leave this at NO.', 'yesno', '1', 3, 24, 0),
(204, 'google_seo_redirect_debug', 'Debug Redirect', 'If you experience infinite redirection loops due to Google SEO Redirect, please enable this option to obtain more information about what is going wrong with your redirect and then report a bug to the plugin author. The debug information is ugly and therefore shown only to board admins.', 'yesno', '1', 4, 24, 0),
(205, 'google_seo_sitemap', 'Google SEO Sitemap', 'This module provides <a href="http://sitemaps.org/">XML Sitemap</a> for your forum. This makes it easier for Google to discover pages on your site. Please see the <a href="../inc/plugins/google_seo.txt">documentation</a> for details.<br /><br />Set to YES to enable Google SEO Sitemap. Setting this to NO also disables all other settings in this group.', 'yesno', '1', 1, 25, 0),
(206, 'google_seo_sitemap_url', 'XML Sitemap URL scheme', 'This is the URL scheme used for the XML Sitemap pages. By default, this is <i>sitemap-{url}.xml</i> and your sitemap will be called <i>sitemap-index.xml</i>. Please note that if you change this, you will also need to add a new rewrite rule to your .htaccess. If your host does not support mod_rewrite, leave this empty. Your sitemap will then be called <i>misc.php?google_seo_sitemap=index</i>.', 'text', 'sitemap-{url}.xml', 2, 25, 0),
(207, 'google_seo_sitemap_forums', 'XML Sitemap Forums', 'Include Forums in the XML Sitemap.', 'radio\n0=No\n1=Yes\n2=Yes, including forum pages', '1', 3, 25, 0),
(208, 'google_seo_sitemap_threads', 'XML Sitemap Threads', 'Include Threads in the XML Sitemap.', 'radio\n0=No\n1=Yes\n2=Yes, including thread pages', '1', 4, 25, 0),
(209, 'google_seo_sitemap_users', 'XML Sitemap Users', 'Include Users in the XML Sitemap.', 'yesno', '1', 5, 25, 0),
(210, 'google_seo_sitemap_announcements', 'XML Sitemap Announcements', 'Include Announcements in the XML Sitemap.', 'yesno', '1', 6, 25, 0),
(211, 'google_seo_sitemap_calendars', 'XML Sitemap Calendars', 'Include Calendars in the XML Sitemap.', 'yesno', '1', 7, 25, 0),
(212, 'google_seo_sitemap_events', 'XML Sitemap Events', 'Include Events in the XML Sitemap.', 'yesno', '1', 8, 25, 0);
INSERT INTO `mybb_settings` (`sid`, `name`, `title`, `description`, `optionscode`, `value`, `disporder`, `gid`, `isdefault`) VALUES
(213, 'google_seo_sitemap_additional', 'XML Sitemap additional pages', 'List of additional URLs relative to your site that should be included in the XML Sitemap. If you have any custom pages you can include them here, one page per line. Entries must be relative to your site, i.e. they must not contain http://, and must not start with .. or /.', 'textarea', 'index.php\r\nportal.php', 9, 25, 0),
(214, 'google_seo_sitemap_pagination', 'XML Sitemap pagination', 'Set the maximum number of items that may appear in a single XML Sitemap before it is split (not counting optional forum/thread pages). Setting it too low will result in too many sitemaps, setting it too high may cause server load every time the sitemap is generated. If unsure, leave at 1000.', 'text', '1000', 10, 25, 0),
(215, 'google_seo_url', 'Enable Google SEO URLs', 'This module replaces the stock MyBB URLs with descriptive URLs that use words (thread subject, forum title, user name, etc) instead of random numeric IDs. Please see the <a href="../inc/plugins/google_seo.txt">documentation</a> for details.<br /><br />Set to YES to enable Google SEO URL. Setting this to NO also disables all other settings in this group.', 'yesno', '1', 1, 26, 0),
(216, 'google_seo_url_punctuation', 'Punctuation characters', 'Punctuation and other special characters are filtered from the URL string and replaced by the separator. By default, this string contains all special ASCII characters including space. If you are running an international forum with non-ASCII script, you might want to add unwanted punctuation characters of those scripts here.', 'text', '!"#$%&''( )*+,-./:;<=>?@[\\]^_`{|}~', 2, 26, 0),
(217, 'google_seo_url_separator', 'URL separator', 'Enter the separator that should be used to separate words in the URLs. By default this is - which is a good choice as it is easy to type in most keyboard layouts (single keypress without shift/alt modifier). If you want some other character or string as a separator, you can enter it here. Please note that special characters like :&amp;?@/ or space could render your URLs unuseable or hard to work with.', 'text', '-', 3, 26, 0),
(218, 'google_seo_url_uniquifier', 'URL uniquifier', 'In case of URL collisions (for example two threads with the same title), the uniquifier is applied to the URL of the newer thread. To guarantee uniqueness, the uniquifier must incorporate the ID and use punctuation other than a single separator. Please see the <a href="../inc/plugins/google_seo.txt">documentation</a> for examples of good and bad uniquifiers.', 'text', '{url}{separator}{separator}{id}', 4, 26, 0),
(219, 'google_seo_url_uniquifier_force', 'URL uniquifier enforcer', 'This option is NOT recommended. If you set this to yes, Google SEO will be forced to use the uniquifier for all URLs without exception, even if it''s not necessary. Use this only if you absolutely want every URL to contain the ID. If you enable this, a single separator will be sufficient for the uniquifier.', 'yesno', '1', 5, 26, 0),
(220, 'google_seo_url_translate', 'Character Translation', 'If you want to replace some characters (German umlaut example: Übergrößenträger =&gt; Uebergroessentraeger) or words in your URLs, please add your translations to <i>inc/plugins/google_seo/translate.php</i> and then enable this option. Please see the <a href="../inc/plugins/google_seo.txt">documentation</a> for details.', 'yesno', '1', 6, 26, 0),
(221, 'google_seo_url_lowercase', 'lowercase words', 'If you prefer lower case URLs, you can set this to YES. This will not affect the way URLs are stored in the database so you can go back to the original case letters any time. Please note that if you set this to YES, you will also have to make sure that your forum URL, as well as scheme and uniqufier are all lowercase too for the URL to be completely in lower case.', 'yesno', '1', 7, 26, 0),
(222, 'google_seo_url_length_soft', 'URL length soft limit', 'URLs can be shortened after a soft limit by truncating it after a word (punctuation separator). Set to 0 to disable.', 'text', '0', 8, 26, 0),
(223, 'google_seo_url_length_hard', 'URL length hard limit', 'URLs can be shortened after a hard limit by truncating it regardless of word separators. Set to 0 to disable.', 'text', '0', 9, 26, 0),
(224, 'google_seo_url_forums', 'Forum URL scheme', 'Enter the Forum URL scheme. By default this is <i>Forum-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Forums.', 'text', 'Forum-{url}', 10, 26, 0),
(225, 'google_seo_url_threads', 'Thread URL scheme', 'Enter the Thread URL scheme. By default this is <i>Thread-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Threads.', 'text', 'Thread-{url}', 11, 26, 0),
(226, 'google_seo_url_announcements', 'Announcement URL scheme', 'Enter the Announcement URL scheme. By default this is <i>Announcement-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Announcements.', 'text', 'Announcement-{url}', 12, 26, 0),
(227, 'google_seo_url_users', 'User URL scheme', 'Enter the User URL scheme. By default this is <i>User-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Users.', 'text', 'User-{url}', 13, 26, 0),
(228, 'google_seo_url_calendars', 'Calendar URL scheme', 'Enter the Calendar URL scheme. By default this is <i>Calendar-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Calendars.', 'text', 'Calendar-{url}', 14, 26, 0),
(229, 'google_seo_url_events', 'Event URL scheme', 'Enter the Event URL scheme. By default this is <i>Event-{url}</i>. Please note that if you change this, you will also need to add a new rewrite rule in your .htaccess file. Leave empty to disable Google SEO URLs for Events.', 'text', 'Event-{url}', 15, 26, 0),
(230, 'ganalytics_webpropertyid', 'Google Analytics web property ID', 'Enter here your Google Analytics web property ID (for example "UA-7207177-6").', 'text', '', 1, 27, 0),
(231, 'profilebuddies_limit', 'Limit Buddies', 'Limits the number of buddies that will be displayed in profile.', 'text', '10', 1, 28, 0),
(232, 'profilebuddies_limit_overwrite', 'Show All Buddies?', 'Do you want to give your members the possibility to view all buddies without limitation?', 'yesno', '0', 2, 28, 0),
(233, 'profilebuddies_order_by', 'Sort Field', 'Select the field that you want buddies to be sorted.', 'select\nusername=Username\nregdate=Registration date\nlastvisit=Last visit\npostnum=Post count\nRAND()=Random', 'username', 3, 28, 0),
(234, 'profilebuddies_order_dir', 'Sort Order', 'Select the order that you want buddies to be sorted.', 'select\nASC=Ascending\nDESC=Descending', 'ASC', 4, 28, 0),
(235, 'profilebuddies_quicklinks', 'Show Quick Links?', 'Do you want to display quick links for adding or removing a buddy?', 'yesno', '0', 5, 28, 0),
(236, 'profilebuddies_email', 'Email Notification', 'If you active this setting your members will get an email notification when someone adds them to buddy list.', 'onoff', '0', 6, 28, 0),
(237, 'profilebuddies_pm', 'Private Message Notification', 'If you active this setting your members will get a private message notification when someone adds them to buddy list.', 'onoff', '0', 7, 28, 0),
(238, 'ps_active', 'Activate', 'Do you want to activate the plugin?\r\n		<style type="text/css">\r\n		#row_setting_ps_active td.first,\r\n		#row_setting_ps_position td.first,\r\n		#row_setting_ps_date_format_ty td.first,\r\n		#row_setting_ps_trow_message_pos td.first,\r\n		#row_setting_ps_latest_posts_pos td.first,\r\n		#row_setting_ps_cell_6 td.first\r\n		{\r\n			border-bottom: 4px solid #016BAE;\r\n			padding-bottom: 40px;\r\n			border-left: 0px;\r\n			border-right: 0px;\r\n			background-repeat: no-repeat;\r\n			background-position: bottom left;\r\n		}\r\n		#row_setting_ps_active td.first {\r\n			background-image: url(../images/prostats/ps_settings_vp.gif);\r\n		}\r\n		#row_setting_ps_position td.first {\r\n			background-image: url(../images/prostats/ps_settings_ga.gif);\r\n		}\r\n		#row_setting_ps_date_format_ty td.first {\r\n			background-image: url(../images/prostats/ps_settings_mb.gif);\r\n		}\r\n		#row_setting_ps_trow_message_pos td.first {\r\n			background-image: url(../images/prostats/ps_settings_lp.gif);\r\n		}\r\n		#row_setting_ps_latest_posts_pos td.first {\r\n			background-image: url(../images/prostats/ps_settings_ec.gif);\r\n		}\r\n		#row_setting_ps_cell_6 td.first {\r\n			background-image: url(../images/prostats/ps_settings_mc.gif);\r\n		}\r\n		#row_setting_ps_version {\r\n			display: none;\r\n		}\r\n		</style>', 'yesno', '1', 1, 29, 0),
(239, 'ps_index', 'Show in index', 'Show the ProStats table in the index page.', 'yesno', '1', 3, 29, 0),
(240, 'ps_portal', 'Show in portal', 'Show the ProStats table in the portal page.', 'yesno', '0', 4, 29, 0),
(241, 'ps_global_tag', 'Activate global tag', 'So you can edit themes and insert &lt;ProStats&gt; tag wherever you want to show the stats.', 'yesno', '0', 5, 29, 0),
(242, 'ps_hidefrombots', 'Hide from search bots', 'Using this option you can hide stats from all search bots you''ve defined them in <strong><a href="index.php?module=config-spiders" target="_blank">Spiders/Bots</a></strong> page. This will save bandwidth and decrease server load.', 'yesno', '1', 6, 29, 0),
(243, 'ps_ignoreforums', 'Ignore list', 'Forums not to be shown on ProStats. Seperate with comma. (e.g. 1,3,12)', 'text', '', 7, 29, 0),
(244, 'ps_position', 'Table position in index and portal', 'Position of stats in index and portal pages.', 'select\n0=Top (Header)\n1=Bottom (Footer)', '1', 10, 29, 0),
(245, 'ps_format_name', 'Style usernames', 'Style the username in true color, font, etc.', 'yesno', '1', 20, 29, 0),
(246, 'ps_subject_length', 'Subject length', 'Maximum length of topic/post subjects. (Input 0 to remove the limitation)', 'text', '25', 30, 29, 0),
(247, 'ps_num_rows', 'Number of rows', 'How much items must be shown? (Input an odd number greater than or equal to 3)', 'text', '11', 41, 29, 0),
(248, 'ps_date_format', 'Date and Time format', 'The format of Date and Time which would be used in stats. [<a href="http://php.net/manual/en/function.date.php" target="_blank">More Information</a>]', 'text', 'm-d, H:i', 42, 29, 0),
(249, 'ps_date_format_ty', 'Replace format', 'A part of Date and Time format that must be replaced with "Yesterday" or "Today".', 'text', 'm-d', 43, 29, 0),
(250, 'ps_trow_message', 'Message block', 'This is a block on top/bottom of the ProStats table that you can put your HTML contents in it. Leave it empty to hide it.', 'textarea', '', 45, 29, 0),
(251, 'ps_trow_message_pos', 'Message block position', 'The position of message block in the ProStats table.', 'select\n0=Top\n1=Down (Default)', '1', 46, 29, 0),
(252, 'ps_latest_posts', 'Show latest posts', 'Show latest posts in the ProStats table.', 'yesno', '1', 50, 29, 0),
(253, 'ps_latest_posts_prefix', 'Show prefix for latest posts', 'Show prefixes in subject of latest posts (if there are any).', 'yesno', '1', 52, 29, 0),
(254, 'ps_latest_posts_cells', 'Stats of latest posts', 'What type of stats you want to be shown for latest posts?<br />Your choices are: <strong>Latest_posts, Date, Starter, Last_sender, Forum</strong><br />Separate them by comma (",").', 'text', 'Latest_posts, Date, Starter, Last_sender, Forum', 55, 29, 0),
(255, 'ps_latest_posts_pos', 'Latest posts position', 'The position of the Latest posts block.', 'select\n0=Left\n1=Right', '0', 60, 29, 0),
